:local(.section_1) {
    position: relative;

    width: 100%;
    height: 100vh;

    background-image: url('~images/main_screen_img.png');
    background-repeat: no-repeat;
    background-position: center center;
    background-size: cover;
}

:local(.facebook_button) {
    position: absolute;
    top: 40px;
    right: 100px;
}

:local(.facebook_button button) {
    background-color: Transparent;
    cursor:pointer;
    color: white;
    font-size: 33px;
    outline:none;
}

:local(.title_1) {
    position: absolute;
    top: 30%;
    left: 50%;
    transform: translate(-50%, -50%);

    text-align: center;
    font-size: 36px;
    font-weight: 800;
    color: white;
}

:local(.section_2) {
    width: 100%;
    height: 450px;
}

:local(.title_2) {
    margin-top: 30px;

    text-align: center;
    font-size: 30px;
    color: #2a3753;
}

:local(.info) {
    margin-top: 40px;
}

:local(.about) {
    margin: 30px 0 0 0;
}

:local(.about div) {
    display: table-cell;
    vertical-align: middle;
    padding: 0 10px;
}

:local(.about) h2{
    font-weight: 600;
    font-size: 18px;

    color: #33536D;
    margin-bottom: 8px;
}

:local(.about) p{
    font-weight: 300;
    font-size: 14px;
}



:local(.world_img) {
    widht: 80px;
    height: 80px;
}

:local(.cool_img) {
    widht: 70px;
    height: 70px;
}

:local(.action_2) {

}

:local(.action_2 h2) {
     margin-top: 30px;

     text-align: center;
     font-size: 30px;
     color: #2a3753;
 }

:local(.action_2 img) {
    widht: 70px;
    height: 70px;

    display: block;
    margin-top: 10px;
    margin-left: auto;
    margin-right: auto;
}

:local(.section_3) {
    width: 100%;
    height: 100vh;
}

:local(.exam) {
    position: relative;
    height: 50vh;
    background-image: url('~images/back_exam.png');
}

:local(.exam h2) {

    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);

    cursor: pointer;

    font-size: 50px;
    color: white;
}

:local(.section_4) {
    width: 100%;
    height: 60px;
}

:local(.popup_exam_container) {
    background-image: url('~images/backgroun_white.png');
}

:local(.popup_exam_header) {
    height: 100px;

    border: 1px solid #2a3753;

    font-size: 30px;
    text-align: center;

    position: relative;
}

:local(.popup_exam_header h2) {
    width: 100%;
    margin: 0;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

:local(.popup_exam_type) {
    height: 150px;

    border: 1px solid #2a3753;

    font-size: 25px;
    text-align: center;

    cursor: pointer;

    position: relative;
}

:local(.popup_exam_type h2) {
    width: 100%;
    margin: 0;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

:local(.footer) {
    padding-top: 15px;
    font-size: 18px;
}
